import React, { useEffect, useState } from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import './NavBar.css'; // Import CSS file

function NavBar() {
  const [navBackground, setNavBackground] = useState(false);

  // Scroll event listener for background change
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 100) {
        setNavBackground(true);
      } else {
        setNavBackground(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <Navbar
      expand="lg"
      className={`navbar ${navBackground ? 'navbar-scrolled' : 'navbar-transparent'}`}
      variant="dark"
    >
      <Container>
      <Navbar.Brand href="#home" className="navbar-brand-animated">
       <img src="src\images\logod.jpeg"  
            // alt="Digvijay Logo"
            style={{ width: '40px', height: '40px', marginRight: '8px' }}/>
            Digvijay
           </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link href="#" className="nav-link-animated">Home</Nav.Link>
            <Nav.Link href="#intro_section" className="nav-link-animated">About</Nav.Link>
            <Nav.Link href="#education_section" className="nav-link-animated">Education</Nav.Link>
            <Nav.Link href="#skills_section" className="nav-link-animated">Skills</Nav.Link>
            <Nav.Link href="#projects_section" className="nav-link-animated">Projects</Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavBar;


